package Chapter6.homework;

//A:求两个数据之和

public class SumDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10,b=20;
		int sum = sum(a,b);
		System.out.println("a和b的和为：" + sum);
	}
	
	public static int sum(int a,int b){
		return a+b;
	}

}
